var d=document,b=d.querySelectorAll('#body')[0],bc=b.classList;function isHasAcceptedPolicy(){return(null!=getCookie().match(/acceptedPolicy=true/))}function getCookie(){return d.cookie}function writeCookie(n,v,t=14){var dt=new Date();dt.setTime(dt.getTime()+t *24*3600*1000);d.cookie=n+'='+v+';expires='+dt.toUTCString()+';'+'domain='+window.location.hostname+';path=/'}function toggleTheme(){bc.toggle('dark-theme');bc.toggle('light-theme');writeCookie('darkTheme',bc.contains('dark-theme'))}function toggleDyslexicFont(){bc.toggle('dyslexic');writeCookie('dyslexic',bc.contains('dyslexic'))}function zoom(s){var fs=(parseInt(b.style.fontSize,10)||12)+s;if(fs>22)fs=24;if(fs<10)fs=8;b.style.fontSize=fs+'pt';writeCookie('fontSize',fs)}function restoreSettingsFromCookie(){if(!isHasAcceptedPolicy()){var b=d.querySelectorAll('.policy-banner')[0];b.style.display='block';return;}if(null!=getCookie().match(/darkTheme=false/)){d.querySelectorAll('#dark-mode').checked=true;toggleTheme()}if(null!=getCookie().match(/dyslexic=true/)){d.querySelectorAll('#dyslexic').checked=true;toggleDyslexicFont()}if(null!=getCookie().match(/fontSize/))zoom((getCookie().match(/(^|)fontSize=([^;]+)/))[2]-12);}restoreSettingsFromCookie()
